import {    REGISTER_USER, GET_AUTH_USER, ERROR_AUTH
} from '../actions/types'

const userinitialstate ={
    user: null,
    currentuser:null,
    loadinguser:false,
    errorusermessage:null
}

export default (state = userinitialstate, action) => {

    switch(action.type){
        case GET_AUTH_USER:
            return {
                ...state,
                currentuser: action.payload,
                loadinguser:false  
            }                
        case REGISTER_USER:
            return {
                ...state
            }
        case ERROR_AUTH:
            return {
               ...state,
               errorusermessage: action.payload,
               loadinguser:false
            }    
        default:{
            return {...state}
        }
             
    }

}
