import React, {useState, useEffect} from 'react'
import M from 'materialize-css/dist/js/materialize.min.js'
import {connect} from 'react-redux'
import propTypes from 'prop-types'
import {createUser} from '../../actions/authActions'

const RegisterUserModal = () => {
    const [userName, SetUserName] = useState('');
    const [userEmail, SetUserEmail] = useState('');
    const [userpassword, SetUserPassword] = useState('');
    const [userpassword2, SetUserPassword2] = useState('');
    const [userRegion, SetUserRegion] = useState('');

    const onSubmit = () => {

       if (userpassword !== userpassword2)
           M.toast({ html:'Password do not match'});  
       else    
         {
            console.log('creating a new user');  
            const new_user = {
              userName: userName,
              userEmail: userEmail,
              userpassword: userpassword,
              userRegion: userRegion,
              createdon:new Date() 
            }
    
            createUser(new_user);         
         }                
    }

  return (
    <div id="registeruser-event-modal"  className='modal'>        
        <div className='row'>
            <div className="col l12 m12 s12">
                <h5 className='center'>Register</h5>
                <div className="input-field col s12">
                <input id="UserName" type="text" className="validate" 
                value={userName}
                onChange={e => SetUserName(e.target.value)}
                />
                <label htmlFor="UserName">Enter name</label>
                </div>
                <div className="input-field col s12">
                <input id="UserEmail" type="email" className="validate" 
                value={userEmail}
                onChange={e => SetUserEmail(e.target.value)}
                />
                <label htmlFor="UserEmail">Enter email</label>
                </div>
                <div className="input-field col s12">
                <input id="UserPassword" type="password" className="validate"
                value={userpassword}
                onChange={e => SetUserPassword(e.target.value)}
                />
                <label htmlFor="UserPassword">Enter password</label>
                </div>
                <div className="input-field col s12">
                <input id="UserPassword2" type="password" className="validate" 
                value={userpassword2}
                onChange={e => SetUserPassword2(e.target.value)}
                />
                <label htmlFor="UserPassword2">Confirm password</label>
                </div>
                <div className="input-field col s12">
                    <select onChange={e => SetUserRegion(e.target.value)} value={userRegion}>
                        <option value="" disabled default>Choose your region</option>
                        <option value="1">Lima</option>
                        <option value="2">Cusco</option>
                        <option value="3">Arequipa</option>
                    </select>
                </div>		  
                <div className="input-field col s12">
                        <a href="#!" 
                        onClick={onSubmit}
                        className='modal-close waves-effect waves-light btn'>Create</a>  			
                </div>
            </div>                       
        </div>
    </div>

    
  )

  
}


export default RegisterUserModal
