import {    GET_EVENTS_PER_REGION, GET_EVENT, 
            ADD_EVENT, DELETE_EVENT,
            UPDATE_EVENT,SEARCH_EVENTS,ERROR_EVENT,SET_CURRENT_EVENT, CLEAR_CURRENT_EVENT
       } from './types'

//GET EVENTS PER REGION
export const getEventsPerRegion = ()  => async dispatch => {

    try{

        const resp = await fetch("http://localhost:5000/events");
        const data = await resp.json();

        dispatch({
            type:  GET_EVENTS_PER_REGION, 
            payload: data
        })


    } 
    catch(error)
    {
        dispatch({
            type:  ERROR_EVENT, 
            payload: error.response.statusText
        })
    }

}

export const addEvent = (event)  => async dispatch => {

    try{

        const resp = await fetch("http://localhost:5000/events",
            {
               method:'POST',
               body:JSON.stringify(event),
               headers: {
                'Content-Type': 'application/json'
                }     
            }                            
        );

        const data = await resp.json();

        dispatch({
            type:  ADD_EVENT, 
            payload: data
        })


    } 
    catch(error)
    {
        dispatch({
            type:  ERROR_EVENT, 
            payload: error.response.statusText
        })
    }

}

//updateEvent
export const updateEvent = (event)  => async dispatch => {

    try{

        const data = await fetch(`http://localhost:5000/events/${event.id}`,
            {
               method:'PUT',
               body:JSON.stringify(event),
               headers: {
                'Content-Type': 'application/json'
                }     
            }                            
        );

        dispatch({
            type:  UPDATE_EVENT, 
            payload: data
        })


    } 
    catch(error)
    {
        dispatch({
            type:  ERROR_EVENT, 
            payload: error.response.statusText
        })
    }

}

//delete an event
export const deleteEvent = (id)  => async dispatch => {

    try{

        
        await fetch(`http://localhost:5000/events/${id}`,
            {
               method:'DELETE'               
            }                            
        );

        
        dispatch({
            type:  DELETE_EVENT, 
            payload: id
        })


    } 
    catch(error)
    {
        dispatch({
            type:  ERROR_EVENT, 
            payload: error.response.statusText
        })
    }

}

//set currentevent
export const clearCurrentEvent = ()  => async dispatch => {

    try{

        dispatch({
            type:  CLEAR_CURRENT_EVENT
        })


    } 
    catch(error)
    {
        dispatch({
            type:  ERROR_EVENT, 
            payload: error.response.statusText
        })
    }

}


//set currentevent
export const setCurrentEvent = (event)  => async dispatch => {

    try{

        dispatch({
            type:  SET_CURRENT_EVENT, 
            payload: event
        })


    } 
    catch(error)
    {
        dispatch({
            type:  ERROR_EVENT, 
            payload: error.response.statusText
        })
    }

}
