import React, {useState, useEffect} from 'react'
import M from 'materialize-css/dist/js/materialize.min.js'

const AddEventModal = () => {

  const [title, SetTitle] = useState('');
  const [description, SetDescription] = useState('');
  const [region, SetRegion] = useState('');
  const [createdBy, SetCreatedBy] = useState('');

  useEffect(() => {
    console.log('01')
    SetRegion('Lima');
    SetCreatedBy('Alex Aguilar');
     //eslint-disable-next-line 
     console.log('02')
  }, []) //once


  const onSubmit = () => {

      console.log(title,description,region);

      if (title === '' || description === '' ){
        M.toast({ html:'Please enter a event title and desciption'}); 
      }
      else{
        const new_event = {
          title: title,
          description: description,
          region: region,
          createdby: createdBy,
          date:new Date() 
        }

        addEvent(new_event);        

      }

      

  }


  return (
    <div id="add-event-modal" className="modal" style={modalStyle}>
        <div className="modal-content">
            <div className='row'>
              <div className='input-field col s12'>
                  <input placeholder="Title" id="event_title" 
                  type="text" 
                  onChange={e => SetTitle(e.target.value)}
                  className='validate' />
                  <label htmlFor='event_title'>Title</label>
              </div>
            </div>                       
            <div className='row'>
              <div className='input-field col s12'>
                  <textarea  placeholder="Description" id="event_description" 
                  type="text" 
                  className='materialize-textarea' 
                  onChange={e=> SetDescription(e.target.value)}
                  />
                  <label htmlFor='event_description'>Description</label>
              </div>
            </div>                       
            <div className='row'>
              <div className='input-field col s12'>
                  <input  id="event_region" type="text" className='validate' />
                  <label htmlFor='event_region'>Region</label>
              </div>
            </div>               
            <div className='modal-footer'>
                <a href="#!" 
                onClick={onSubmit}
                className='waves-effect waves-light btn'>Enter</a>
            </div> 
        </div>
    </div>    
  )
}

const modalStyle = {
    with: '75%',
    height:'75%'
};


export default AddEventModal
