import {    LOGIN_USER, REGISTER_USER
} from '../actions/types'

const userinitialstate ={
    user: null,
    currentuser:null,
    loadinguser:false,
    erroruser:null
}

export default (state = userinitialstate, action) => {

    switch(action.type){
        case LOGIN_USER:
            return {
                ...state,
                currentuser: action.payload,
                loadinguser:false  
            }                
        case REGISTER_USER:
            return state;
        default:
            return state; 
    }

}
