import React, {useEffect} from 'react'
import Event from './Event'
import {connect} from 'react-redux'
import {getEventsPerRegion} from '../../actions/eventActions'
import propTypes from 'prop-types'


const Events = ({event :{events, current, loading}, getEventsPerRegion}) => {


  useEffect(() => {
    getEventsPerRegion();
     //eslint-disable-next-line 
  }, [] ) //once


  const renderEventRow = (events) => {

    console.log(events);
    
    const eventrowdiv = events.map((tmpevent) => {return (
                      <div className="col s12 m4 center">
                          <Event></Event>
                          {tmpevent.title}
                      </div>
                ) })

    return(
      <div className="row">
        {eventrowdiv}
      </div>
      )   

  }

  return (
    renderEventRow(events)    
    // <div class="row">      
    //   <div class="col s4 m1">Primer contenido</div>
    //   <div class="col s4 m1">Segundo Contenido</div>
    //   <div class="col s4 m1">tercer contenido</div>
    // </div>
    
  );
}

Events.propTypes = {
  getEventsPerRegion: propTypes.func.isRequired
}

const mapStatetoProps = state => ({
  event: state.event
})

export default connect(mapStatetoProps, {getEventsPerRegion})(Events)
