import {  LOGIN_USER, REGISTER_USER, GET_AUTH_USER  
} from './types'

//AUTH USER
export const authenticateUser = (user)  => async dispatch => {

    try{
        const vemail = user.loginUserEmail;
        const vpassword =user.loginUserPassword;

        console.log(vemail);
        console.log(vpassword);

        const resp = await fetch("http://localhost:5000/users?username=" + vemail + "&password=" + vpassword);
        const data = await resp.json();

        dispatch({
            type:  GET_AUTH_USER, 
            payload: data
        })


    } 
    catch(error)
    {
        dispatch({
            type:  ERROR_EVENT, 
            payload: error.response.statusText
        })
    }


}



