import React, {useEffect} from 'react'
import EventItem from './EventItem'
import {connect} from 'react-redux'
import {getEventsPerRegion} from '../../actions/eventActions'
import propTypes from 'prop-types'


const Events = ({event :{events, current, loading}, getEventsPerRegion}) => {


  useEffect(() => {
    console.log('entro aqui 01')
    getEventsPerRegion();
     //eslint-disable-next-line 
     console.log('entro aqui 02')
  }, []) //once

  
  const renderEventRow = (events) => {

    var arrrenderEventRowGroup = [];
    
    if (events != null){

        var i=0;
        var group=0;
        var eventGroup = [];        

        events.forEach((el)=>{
            i = i + 1;
            eventGroup.push(el);
            if (i %3 === 0){
              group = group + 1;
              arrrenderEventRowGroup.push(renderEventRowGroup(group, eventGroup));  
              eventGroup = [];
            }
            
            console.log(i)
        })

        if  (eventGroup.length > 0){
              group = group + 1;
              arrrenderEventRowGroup.push(renderEventRowGroup(group, eventGroup));  

        }


    }

    return arrrenderEventRowGroup;

  }
  
  const renderEventRowGroup = (group, eventGroup) => {

    if (eventGroup != null){

      const eventrowdiv =  eventGroup.map((tmpevent,index) =>  {
                            console.log('entro al loop '+ index);
                            
                            return (
                              <div className="col s12 m4 center" key={tmpevent.id}>
                                  <EventItem key={tmpevent.id} event={tmpevent}></EventItem>  
                              </div>
                              )                                  
                          }
                  )       
      
      return  <div className="row" key={group}>
                  {eventrowdiv}
              </div> 
  }
  else{
    return(        
        <h4>Empty list</h4>
      )   
  }

  
  }

  return (        
    renderEventRow(events)            
  );
}

Events.propTypes = {
  getEventsPerRegion: propTypes.func.isRequired
}

const mapStatetoProps = state => ({
  event: state.event
})

export default connect(mapStatetoProps, {getEventsPerRegion})(Events)
