import {    REGISTER_USER, GET_AUTH_USER, ERROR_USER, EXISTS_AUTH_USER, NEW_REGISTRATION_ACTION
} from '../actions/types'

const userinitialstate ={
    user: null,
    currentuser:null,
    existsuser:null,
    loadinguser:false,    
    errormessage:null,
    submittedtime: null,
    newregistrationaction:null,
    isauthenticated:false
}

export default (state = userinitialstate, action) => {

    switch(action.type){
        case GET_AUTH_USER:
            return {
                ...state,
                currentuser: action.payload,
                errormessage:null,
                loadinguser:false,
                isauthenticated:true  
            }                
        case REGISTER_USER:
            return {
                ...state,
                currentuser: null,
                user: action.payload,
                errormessage:null,
                isauthenticated:false
            }   
        case EXISTS_AUTH_USER:
            return {
                ...state,
                existsuser: action.payload,
                currentuser:null,
                errormessage:null,
                submittedtime:new Date().getTime(),
                isauthenticated:false
            }            
        case ERROR_USER:
            return {
               ...state,
               currentuser:null,
               errormessage: action.payload,
               loadinguser:false,
               isauthenticated:false
            }
        case NEW_REGISTRATION_ACTION:
            return {
               ...state,
               user: null,
               currentuser:null,
               existsuser:null,
               loadinguser:false,    
               errormessage:null,
               submittedtime: null,
               newregistrationaction: new Date().getTime(),
               loadinguser:false,
               isauthenticated:false
            }        
        default:{
            return {...state, 
                isauthenticated:false,
                currentuser:null}
        }
             
    }

}
