import React, {useState, useEffect} from 'react'
import M from 'materialize-css/dist/js/materialize.min.js'
import {connect} from 'react-redux'
import propTypes from 'prop-types'
import {updateEvent} from '../../actions/eventActions'

const EditEventModal = ({current, updateEvent}) => {

  const [title, SetTitle] = useState('');
  const [description, SetDescription] = useState('');
  const [region, SetRegion] = useState('');
  const [createdBy, SetCreatedBy] = useState('');

  useEffect(() => {

      if (current){
        SetTitle(current.title);
        SetDescription(current.description);  
        SetRegion(current.region);
        SetCreatedBy(current.createdby);
      }

  }) 
  
  
  function onEdit(){

      console.log('editing an event');
      updateEvent();

  }


  return (
    <div id="edit-event-modal" className="modal" style={modalStyle}>
        <div className="modal-content">
            <div className='row'>
              <div className='input-field col s12'>
                  <input placeholder="Title" id="event_title" type="text" className='validate' />
                  <label htmlFor='event_title'>Title</label>
              </div>
            </div>                       
            <div className='row'>
              <div className='input-field col s12'>
                  <textarea  placeholder="Description" id="event_description" type="text" className='materialize-textarea' />
                  <label htmlFor='event_description'>Description</label>
              </div>
            </div>                       
            <div className='row'>
              <div className='input-field col s12'>
                  <input  id="event_region" type="text" className='validate' />
                  <label htmlFor='event_region'>Region</label>
              </div>
            </div>               
            <div className='modal-footer'>
                <a href="#!" className='waves-effect waves-light btn'
                 onClick={onEdit}
                >Enter</a>
            </div> 
        </div>
    </div>    
  )
}

const modalStyle = {
    with: '75%',
    height:'75%'
};

EditEventModal.propTypes = {
  current: propTypes.object,
  updateEvent: propTypes.func.isRequired
}

const mapStateToProps = state => ({  
  current:  state.event.current
})

export default connect(mapStateToProps, updateEvent)(EditEventModal)
