import React, {useEffect} from 'react'
import Event from './Event'
import {connect} from 'react-redux'
import {getEventsPerRegion} from '../../actions/eventActions'
import propTypes from 'prop-types'


const Events = ({event :{events, current, loading}, getEventsPerRegion}) => {


  useEffect(() => {
    console.log('entro aqui 01')
    getEventsPerRegion();
     //eslint-disable-next-line 
     console.log('entro aqui 02')
  }, []) //once


  const renderEventRow = (events) => {
    
    if (events != null){
        console.log(events);
        

       const eventrowdiv = events.map((tmpevent,index) => {
            var vrow =  "";
            var vcolumn =  "";
            var vgroup =  "";
            var vcount = 0;
            var groupindex = (index + 1) % 3;


            console.log('groupindex '+ groupindex);

            vcolumn = <div className="col s12 m4 center" key={tmpevent.id}>
                          <Event key={tmpevent.id} event={tmpevent}></Event>                          
                        </div>   

            if (groupindex === 0){
              vcount = vcount +1;
              vgroup =  <div className="row" key={vcount}>
                          {vcolumn}
                        </div>              
            }
            
        return (
                    //{vgroup}  
                    <h2 key={vcount}>Hi</h2>
                       
                 ) })

        console.log('verificar');
        console.log(eventrowdiv);         

        return(
        <div className="row">
          {eventrowdiv}
        </div>
        )  
      // return(
      //   <div className="row">
      //     <h4>Data list</h4>
      //   </div>
      //   )  
    }
    else{
      return(
        <div className="row">
          <h4>Empty list</h4>
        </div>
        )   
    }
    
  }

  return (    
    renderEventRow(events)    
    // <div class="row">      
    //   <div class="col s4 m1">Primer contenido</div>
    //   <div class="col s4 m1">Segundo Contenido</div>
    //   <div class="col s4 m1">tercer contenido</div>
    // </div>
    
  );
}

Events.propTypes = {
  getEventsPerRegion: propTypes.func.isRequired
}

const mapStatetoProps = state => ({
  event: state.event
})

export default connect(mapStatetoProps, {getEventsPerRegion})(Events)
