import React, {useEffect} from 'react'
import Event from './Event'
import {connect} from 'react-redux'
import {getEventsPerRegion} from '../../actions/eventActions'
import propTypes from 'prop-types'


const Events = ({event :{events, current, loading}, getEventsPerRegion}) => {


  useEffect(() => {
    console.log('entro aqui 01')
    getEventsPerRegion();
     //eslint-disable-next-line 
     console.log('entro aqui 02')
  }, []) //once

  
  const renderEventRow = (events) => {

    if (events != null){

        var i=0;
        var eventGroup = [];

        events.forEach((el)=>{
            i = i + 1;
            eventGroup.push(el);
            if (i %3 === 0){
              renderEventRowGroup(eventGroup);  
              eventGroup = [];
            }
            
            console.log(i)
        })
    }
  }
  
  const renderEventRowGroup = (eventGroup) => {

    if (eventGroup != null){

      const eventrowdiv =  eventGroup.map((tmpevent,index) =>  {
                            console.log('entro al loop '+ index);
                            var groupindex = (index + 1) % 3;

                            return (
                              <div className="col s12 m4 center" key={tmpevent.id}>
                                  <Event key={tmpevent.id} event={tmpevent}></Event>  
                              </div>
                              )                                  
                          }
                  )       
      
      return  <div className="row">
                  {eventrowdiv}
              </div> 
  }
  else{
    return(        
        <h4>Empty list</h4>
      )   
  }

  
  }

  return (    
    //<h4>Hola</h4>
    renderEventRow(events)    
    // <div className="row">      
    //   <div className="col s4 m1">Primer contenido</div>
    //   <div className="col s4 m1">Segundo Contenido</div>
    //   <div className="col s4 m1">tercer contenido</div>
    // </div>
    
  );
}

Events.propTypes = {
  getEventsPerRegion: propTypes.func.isRequired
}

const mapStatetoProps = state => ({
  event: state.event
})

export default connect(mapStatetoProps, {getEventsPerRegion})(Events)
